    private void bSumaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bSumaActionPerformed
        
        try {
            
            double n1 = Double.parseDouble(vNumero1.getText());
            double n2 = Double.parseDouble(vNumero2.getText());
            double resultado = n1+n2;
            vResultado.setText(""+resultado);
            
            
            
            
        } catch (Exception NumberFormatException) {
            
            JOptionPane.showMessageDialog(this, "El valor introducido no es correcto");
            
        }
        
        
        
        
        
        
        
        
    }//GEN-LAST:event_bSumaActionPerformed

