    public void agua(int x, int y) {

        try {
            for (int tamano = 0; tamano < 100; tamano = tamano + 20) {
                Graphics circulo = panel.getGraphics();
                Thread.sleep(100);
                circulo.drawOval(x-tamano/2, y-tamano/2, tamano, tamano);
                //circulo.clearRect(1, 1, tamano, tamano);
                repaint();
            }
        } catch (Exception e) {
            System.out.println("ERROR DESCONOCIDO");
        }

    }


    private void formMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_formMouseClicked
        int x, y;
        x = evt.getX();
        y = evt.getY();
        agua(x, y);

    }//GEN-LAST:event_formMouseClicked

